import timeclass timer():    def __init__(self):        self.flag=0    def start(self):        self.flag+=1        print("计时开始")        self.st=time.time()    def stop(self):        if self.flag ==0:            print("先开始计时")        elif self.flag ==1:            print("计时结束")            self.flag-=1            self.sp=time.time()    def __str__(self):        re="本计时器共运行了%.2f秒"%(self.sp-self.st)        return re    def __sub__(self, other):        return self.st-other.st    def __add__(self, other):        return (self.sp-self.st)+(other.sp-other.st)t=timer()t1=timer()t.start()time.sleep(2)t1.start()time.sleep(3)t.stop()t1.stop()print("差:%d"%int(t1-t))print("共:%d"%int(t1+t))print(t)